<?php	//if id is set, load data	if (isset($_GET['id']))	{		$id = (int)$_GET['id'];		global $wpdb;		$sgPopupData = array();		$sql = $wpdb->prepare("SELECT * FROM ". $wpdb->prefix ."sg_promotional_popup WHERE ID = %d",$id);		$sgPopupData = $wpdb->get_row($sql);		$jsonData = json_decode($sgPopupData->options);		$title = $jsonData->title;		$effect = $jsonData->effect;		$theme = $jsonData->theme;		$duration = $jsonData->duration;		$delay = $jsonData->delay;		$sgEscKey = $jsonData->escKey;		$sgScrolling = $jsonData->scrolling;		$sgCloseButton = $jsonData->closeButton;		$sgReposition = $jsonData->reposition;		$sgOverlayClose = $jsonData->overlayClose;		$sgOpacity = $jsonData->opacity;		$sgPopupFixed = $jsonData->popupFixed;		$sgFixedPostion = $jsonData->fixedPostion;		$sgMaxWidth = $jsonData->maxWidth;		$sgMaxHeight = $jsonData->maxHeight;		$sgInitialWidth = $jsonData->initialWidth;		$sgInitialHeight = $jsonData->initialHeight;		$sgWidth = $jsonData->width;		$sgHeight = $jsonData->height;		$imageStatus  =  '';		$htmlStatus = '';		if($sgPopupData->content == "image") {			$imageStatus = "checked";		}		else if($sgPopupData->content == "html") 		{ 			$htmlStatus = "checked"; 		}	}	$activeImageVariable  = "";	$activeHtmlVariable = "";	$imageStatus  =  '';	$htmlStatus = '';	if(!isset($sgPopupData->content) || $sgPopupData->content == "Image")	{		$imageStatus = "checked";		$activeHtmlVariable = "";		$activeImageVariable = "active";	}	if($sgPopupData->content == "html")	{		$htmlStatus = "checked";		$activeHtmlVariable = "active";		$activeImageVariable = "";	}	//set intial values 	$colorbox_deafult_values = array('escKey'=> true,'closeButton' => true,'scale'=> true, 'scrolling'=> true,'opacity'=>0.8,'reposition' => true,'width' => false,'height' => false,'initialWidth'=>'300','initialHeight'=>'100','maxWidth'=>false,'maxHeight'=>false,'overlayClose'=>true,'fixed'=>false,'top'=>false,'right'=>false,'bottom'=>false,'left'=>false,"duration"=>1,"delay"=>0);	$escKey = ($colorbox_deafult_values['escKey'] == true ? 'checked' : '');	$closeButton = ($colorbox_deafult_values['closeButton'] == true ? 'checked' : '');	$scale = ($colorbox_deafult_values['scale'] == true ? 'checked' : '');	$scrolling = ($colorbox_deafult_values['scale'] == true ? 'checked' : '');	$width = $colorbox_deafult_values['width'];	$height = $colorbox_deafult_values['height'];	$reposition = ($colorbox_deafult_values['reposition'] == true ? 'checked' : '');	$overlayClose = ($colorbox_deafult_values['overlayClose'] == true ? 'checked' : '');	$opacityValue = $colorbox_deafult_values['opacity'];	$top = $colorbox_deafult_values['top'];	$right = $colorbox_deafult_values['right'];	$bottom = $colorbox_deafult_values['bottom'];	$left = $colorbox_deafult_values['left'];	$initialWidth = $colorbox_deafult_values['initialWidth'];	$initialHeight = $colorbox_deafult_values['initialHeight'];	$maxWidth = $colorbox_deafult_values['maxWidth'];	$maxHeight = $colorbox_deafult_values['maxHeight'];	$deafultFixed = $colorbox_deafult_values['fixed'];	$defaultDuration = $colorbox_deafult_values['duration'];	$defaultDelay = $colorbox_deafult_values['delay'];	//seted value	if(isset($sgEscKey)) {$sgEscKey = ($sgEscKey == '') ? '': 'checked'; } else {$sgEscKey = $escKey;}	if(isset($sgCloseButton)) {$sgCloseButton = ($sgCloseButton == '') ? '': 'checked'; } else {$sgCloseButton = $closeButton;}	if(isset($sgScrolling)) {$sgScrolling = ($sgScrolling == '') ? '': 'checked'; } else {$sgScrolling = $scrolling;}	if(isset($sgReposition)) {$sgReposition = ($sgReposition == '') ? '': 'checked'; } else {$sgReposition = $reposition;}	if(isset($sgOverlayClose)) {$sgOverlayClose = ($sgOverlayClose == '') ? '': 'checked'; } else {$sgOverlayClose = $overlayClose;}	if(isset($sgPopupFixed)) {$sgPopupFixed= ($sgPopupFixed == '') ? '': 'checked'; } else {$sgPopupFixed = $deafultFixed;}	if(!isset($sgOpacity )) {$sgOpacity = $opacityValue;}	if(!isset($sgWidth )) {$sgWidth = $width;}	if(!isset($sgHeight )) {$sgHeight = $height;}	if(!isset($sgInitialWidth )) {$sgInitialWidth = $initialWidth;}	if(!isset($sgInitialHeight)) {$sgInitialHeight = $initialHeight;}	if(!isset($sgMaxWidth)) {$sgMaxWidth = $maxWidth;}	if(!isset($sgMaxWidth)) {$sgMaxHeight = $maxHeight;}	if(!isset($duration)) {$duration = $defaultDuration;}	if(!isset($delay)) {$delay = $defaultDelay;}	//select basa value or deafult value		$sg_popup_effects = array("No effect"=>"No Effect","flip"=>"flip","shake"=>"shake","wobble"=>"wobble","swing"=>"swing","flash"=>"flash","bounce"=>"bounce","pulse"=>"pulse","rubberBand"=>"rubberBand","tada"=>"tada","fadeIn"=>"fadeIn");	$sg_popup_theme = array("colorbox1.css","colorbox2.css","colorbox3.css","colorbox4.css","colorbox5.css");	function creaeSelect($options,$name,$selecteOption)	{		$selected ='';		$str = "";		$checked = "";		if($name == 'theme') {							$popup_style_name = 'popup_theme_name';				$firstOption = array_shift($options);				$i = 1;				foreach($options as $key){						if($key == $selecteOption) {						$checked = "checked";					}					else {						$checked ='';					}					$i++;					$str .= "<input type='radio' name=\"$name\" value=\"$key\" $checked class='popup_theme_name' sgPoupNumber=".$i.">";						}				if ($checked == ''){					$checked = "checked";				}				$str = "<input type='radio' name=\"$name\" value=\"".$firstOption."\" $checked class='popup_theme_name' sgPoupNumber='1'>".$str;				return $str;		}		else {			$str .= "<select name=$name id='sameWidthinputs' class=$popup_style_name >";			foreach($options as $key=>$option) {					if($key == $selecteOption) {					$selected = "selected";				}				else {					$selected ='';				}						$str .= "<option value='".$key."' ".$selected."  >$option</potion>";					}			$str .="</select>" ;			return $str;		}	} ?>	<?php		if(isset($_GET['saved']) && $_GET['saved']==1)		{			echo '<div id="defaultMessage" class="updated notice notice-success is-dismissible" ><p>Popup was saved</p></div>';		}	?><form method="POST" action="" id="add-form"><input type="hidden" name="action" value="save_popup">	<div class="crudWrapper">		<div class="cereateTitleWrapper">			<div class="Sg_title_crud">				<?php if(isset($id))					{ ?>						<h1>Edit popup</h1>				<?php }				else  { ?>					<h1>Create new popup</h1>			<?php } ?>			</div>			<p class="submit">				<input type="button" id="promotionalSaveButton" class="button-primary" value="<?php _e('Save Changes') ?>" />				<img id="createAjaxLoader" src="<?php echo plugins_url('img/wpspin_light.gif', dirname(__FILE__).'../');?>" style="display: none">			</p>		</div>		<div class="clear"></div>		<div class="generalWrapper">			<div id="leftMainDiv">				<div class="general">					<div class="h2Background" id="generalTitle">						<h2>General</h2>						<img src="<?php echo plugins_url('img/down_arrow-2x.gif', dirname(__FILE__).'../');?>">					</div>					<div class="generalContent">						<div class="popupTitle">							<span class="createDescribe " >Title*:</span><input class='sameWidthinputs'  id= 'popupTitleValue' type="text" name="title" value="<?php echo esc_attr($title)?>"  required = "required"/><span class="phpErrorStyle"><?php echo $errorTitle;?></span>						</div>						<div class="tabWrapper">							<div class="tabs">								<ul class="tab-links">									<li class="<?php echo $activeHtmlVariable;?>" id="imageLink"><a href="#tab1" class="imageHref" >Image</a></li>									<li class="<?php echo $activeImageVariable;?>" id="htmlLink"><a href="#tab2"  class="htmlHref">Html</a></li>								</ul>								<div class="tab-content">									<div id="tab1" class="tab <?php echo $activeImageVariable;?> ">										<div class="imagetype">											<h1 class="imageHeadline">Please choose your picture</h1>											<div class="imageUploderWrapper">												<input  type="radio" name="content"  class="imageRadioButton" value="Image" <?php echo esc_html($imageStatus);?> id="displayNone" />												<input class='sameWidthinputs' id="upload_image" type="text" size="36" name="ad_image" value="<?php echo esc_attr($sgPopupData->image); ?>"  /> <input id="upload_image_button" class="button sameWidthinputs" type="button" value="Select image"  />											</div>											<div class="ShowSelectedImage">												<span class="NoImage">(No image selected)</span>											</div>										</div>									</div>									<div id="tab2" class="tab <?php echo $activeHtmlVariable;?> ">										<div class="htmlType">										<?php																							$content = wp_kses_post($sgPopupData->html);												$editor_id = 'sg_popup_html';												$settings = array(													'teeny' => true,													'tinymce' => array(														'width' => '100%',													),													'textarea_rows' => '6',													 'editor_css' => '<style> #mceu_27-body{														 width: 100%;													 } </style>',													'media_buttons' => true												);												wp_editor( $content, $editor_id, $settings ); 											?>										</div>									<input type="radio" name="content" value="html" class="htmlRadioButton " <?php echo $htmlStatus;?> id="displayNone" />									</div>								</div>							</div>						</div>						<span class="createDescribe" id="themeSPan">Popup theme:</span><?php echo creaeSelect($sg_popup_theme,'theme',esc_html($theme));?><div class="theme1" id="displayNone"></div><div class="theme2" id="displayNone"></div><div class="theme3" id="displayNone" ></div><div class="theme4" id="displayNone" ></div><div class="theme5" id="displayNone"></div>					</div>				</div>				<div class="effects">					<div class="h2Background" id="effectsTitle">						<h2>Effects</h2>						<img src="<?php echo plugins_url('img/down_arrow-2x.gif', dirname(__FILE__).'../');?>">					</div>					<div class="effectsContent">						<span class="crea